'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var round = require( '@stdlib/math/base/special/round' );
var pow = require( '@stdlib/math/base/special/pow' );
var toFloat32 = require( '@stdlib/types/number/float64/base/to-float32' );
var exponentf = require( './../lib' );

var frac;
var exp;
var x;
var e;
var i;

// Generate random numbers and extract their exponents...
for ( i = 0; i < 100; i++ ) {
	frac = randu() * 10.0;
	exp = round( randu()*44.0 ) - 22;
	x = frac * pow( 10.0, exp );
	x = toFloat32( x );
	e = exponentf( x );
	console.log( 'x: %d. unbiased exponent: %d.', x, e );
}
