'use strict';

// MODULES //

var tape = require( 'tape' );
var isNonNegativeInteger = require( '@stdlib/assert/is-nonnegative-integer' ).isPrimitive;
var hasOwnProp = require( '@stdlib/assert/has-own-property' );
var CompactAdjacencyMatrix = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof CompactAdjacencyMatrix, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to the prototype of the main export is a `nvertices` method', function test( t ) {
	t.strictEqual( hasOwnProp( CompactAdjacencyMatrix.prototype, 'nvertices' ), true, 'has property' );
	t.end();
});

tape( 'the method returns a non negative value denoting number of vertices', function test( t ) {
	var adj;

	adj = new CompactAdjacencyMatrix( 4 );

	adj.addEdge( 1, 0 );
	adj.addEdge( 1, 2 );
	adj.addEdge( 0, 2 );
	adj.addEdge( 2, 3 );

	t.strictEqual( isNonNegativeInteger( adj.nvertices ), true, 'returns expected value' );
	t.end();
});
