'use strict';

var randu = require( '@stdlib/random/base/randu' );
var deepGet = require( './../lib' );

var data;
var keys;
var val;
var i;

data = new Array( 100 );
for ( i = 0; i < data.length; i++ ) {
	data[ i ] = {
		'x': Date.now(),
		'y': [ randu(), randu(), i ]
	};
}

keys = [ 0, 'y', 2 ];
for ( i = 0; i < data.length; i++ ) {
	keys[ 0 ] = i;
	val = deepGet( data, keys );
	console.log( val );
}
