'use strict';

// MODULES //

var tape = require( 'tape' );
var factory = require( './../lib/factory.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof factory, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function throws an error if provided a key path argument which is neither a string primitive or a key array', function test( t ) {
	var values;
	var i;

	values = [
		5,
		null,
		undefined,
		NaN,
		true,
		{},
		function(){}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws when provided a ' + ( typeof values[i] ) );
	}
	t.end();

	function badValue( value ) {
		return function() {
			factory( value );
		};
	}
});

tape( 'the function throws an error if provided an options argument which is not an object', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		5,
		null,
		NaN,
		true,
		undefined,
		[],
		function(){}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws when provided a ' + ( typeof values[i] ) );
	}
	t.end();

	function badValue( value ) {
		return function() {
			factory( 'a', value );
		};
	}
});

tape( 'the function throws an error if provided an invalid option', function test( t ) {
	var values;
	var i;

	values = [
		5,
		null,
		true,
		NaN,
		{},
		undefined,
		function(){}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws when provided a ' + ( typeof values[i] ) );
	}
	t.end();

	function badValue( value ) {
		return function() {
			factory( 'a', {
				'sep': value
			});
		};
	}
});

tape( 'the function returns a function', function test( t ) {
	var dget = factory( 'a/b', {
		'sep': '/'
	});
	t.equal( typeof dget, 'function', 'returns a function' );
	t.end();
});

tape( 'the created function returns `undefined` if provided a non-object or null', function test( t ) {
	var values;
	var dget;
	var i;

	values = [
		'5',
		5,
		null,
		undefined,
		NaN,
		true,
		function(){}
	];

	dget = factory( 'a/b', {
		'sep': '/'
	});

	for ( i = 0; i < values.length; i++ ) {
		t.equal( dget( values[ i ] ), undefined, 'returns undefined' );
	}
	t.end();
});

tape( 'the created function deep gets', function test( t ) {
	var expected;
	var actual;
	var dget;
	var obj;

	dget = factory( ['a','b'] );
	obj = { 'a': { 'b': 999 } };

	actual = dget( obj );
	expected = 999;

	t.strictEqual( actual, expected, 'deep gets value' );
	t.end();
});
