// TypeScript Version: 2.0

/**
* Invokes a function after a specified number of milliseconds.
*
* @private
* @param fcn - function to invoke
* @param wait - wait duration (in milliseconds)
* @param args - arguments to invoke `fcn` with
* @returns timeout object
*
* @example
* function beep() {
*     console.log( 'beep' );
* }
*
* var t = delay( beep, 3000 );
*
* var status = t.status;
* // returns 0
*
* t.clear();
*
* status = t.status;
* // returns 2
*
* t.invoke();
*
* var duration = t.duration;
* // returns 3000
*/
declare function delay( fcn: Function, wait: number, ...args: Array<any> ): TimeoutObject; // tslint-disable-line max-line-length

// EXPORTS //

export = delay;

