import delay = require( './index' );


// TESTS //

// The function returns a timeout object...
{
	delay( () => {}, 2000 ); // $ExpectType TimeoutObject
}

// The compiler throws an error if the function is provided a first argument which is not a function...
{
	delay( 'beep', 2000 ); // $ExpectError
	delay( 5, 2000 ); // $ExpectError
	delay( true, 2000 ); // $ExpectError
	delay( false, 2000 ); // $ExpectError
	delay( null, 2000 ); // $ExpectError

	delay( [], 2000 ); // $ExpectError
	delay( {}, 2000 ); // $ExpectError
}

// The compiler throws an error if the function is provided a second argument which is not a number...
{
	delay( () => {}, 'beep' ); // $ExpectError
	delay( () => {}, true ); // $ExpectError
	delay( () => {}, false ); // $ExpectError
	delay( () => {}, null ); // $ExpectError
	delay( () => {}, [] ); // $ExpectError
	delay( () => {}, {} ); // $ExpectError
}

// The compiler throws an error if the function is provided an incorrect number of arguments...
{
	delay(); // $ExpectError
	delay( () => {} ); // $ExpectError
	delay( () => {}, 2000, 'beep' ); // $ExpectError
}


