'use strict';

var flattenObject = require( './../lib' );

var obj = {
	'a': {
		'b': {
			'beep': 'boop',
			'foo': 'bar'
		},
		'c': [ 1, 2, 3 ],
		'd': true,
		'e': null,
		'f': 3.14,
		'g': new Date(),
		'h': {
			'bip': 6,
			'bop': [ 4, 5, 6 ]
		},
		'i': [ null, true, {} ],
		'j': /.*/,
		'k': function noop(){},
		'l': NaN,
		'm': [],
		'n': new Buffer( 'hello' ),
		'o': {
			'data': new Float64Array( 10 )
		},
		'p': {
			'1': {
				'2': {
					'3': {}
				}
			}
		}
	}
};

var out = flattenObject( obj, {
	'depth': 5,
	'copy': true,
	'flattenArrays': false,
	'delimiter': '-|-'
});
console.dir( out );
