// TypeScript Version: 4.1

/// <reference types="@stdlib/types"/>

import { Collection } from '@stdlib/types/array';

interface Options<T> {
	/**
	* Execution context.
	*/
	thisArg?: ThisParameterType<Indicator<T>>;
}

/**
* Specifies which group a property belongs to.
*
* @returns group key
*/
type Nullary = () => string | symbol;

/**
* Specifies which group a property belongs to.
*
* @param value - object value
* @returns group key
*/
type Unary<T> = ( value: T ) => string | symbol;

/**
* Specifies which group a property belongs to.
*
* @param value - object value
* @param key - object key
* @returns group key
*/
type Binary<T> = ( value: T, key: string | symbol ) => string | symbol;

/**
* Specifies which group a property belongs to.
*
* @param value - object value
* @param key - object key
* @returns group key
*/
type Indicator<T> = Nullary | Unary<T> | Binary<T>;

interface IndicesAndValuesOptions<T> extends Options<T> {
	/**
	* Specifies that indices and values should be returned.
	*/
	returns: '*';
}
interface IndicesOptions<T> extends Options<T> {
	/**
	* Specifies that indices should be returned.
	*/
	returns: 'indices';
}

interface ValuesResults<T> {
	/**
	* Object properties.
	*/
	[key: string | symbol]: Array<T>;
}

interface IndicesResults {
	/**
	* Object properties.
	*/
	[key: string | symbol | number]: Array<number>;
}

interface IndicesAndValuesResults<T> {
	/**
	* Object properties.
	*/
	[key: string | symbol | number]: Array<[ number, T ]>;
}

/**
* Groups an object's own and inherited property values according to an indicator function.
*
* ## Notes
*
* -   When invoked, the indicator function is provided two arguments:
*
*     -   `value`: object value
*     -   `key`: object key
*
* -   The value returned by an indicator function should be a value which can be serialized as an object key.
*
* -   If provided an empty object with no prototype, the function returns an empty object.
*
* -   The function iterates over an object's own and inherited properties.
*
* -   Key iteration order is **not** guaranteed, and, thus, result order is **not** guaranteed.
*
* @param obj - input object
* @param indicator - indicator function indicating which group an element in the input object belongs to
* @returns group results
*
* @example
* function indicator( v ) {
*     return v[ 0 ];
* }
*
* function Foo() {
*     this.a = 'beep';
*     this.b = 'boop';
*     return this;
* }
*
* Foo.prototype = Object.create( null );
* Foo.prototype.c = 'foo';
* Foo.prototype.d = 'bar';
*
* var obj = new Foo();
*
* var out = groupIn( obj, indicator );
* // e.g., returns { 'b': [ 'beep', 'boop', 'bar' ], 'f': [ 'foo' ] }
*/

declare function groupIn<T = unknown>( collection: Collection<T>, indicator: Indicator<T> ): ValuesResults<T>;

/**
* Groups values according to an indicator function.
*
* ## Notes
*
* -   When invoked, the indicator function is provided two arguments:
*
*     -   `value`: collection value
*     -   `index`: collection index
*
* -   The value returned by an indicator function should be a value which can be serialized as an object key.
*
* -   If provided an empty collection, the function returns an empty object.
*
* @param collection - collection to group
* @param options - function options
* @param options.thisArg - execution context
* @param options.returns - if `values`, values are returned; if `indices`, indices are returned; if `*`, both indices and values are returned (default: 'values')
* @param indicator - indicator function specifying which group an element in the input collection belongs to
* @returns group results
*
* @example
* function indicator( v ) {
*     return v[ 0 ];
* }
* var arr = [ 'beep', 'boop', 'foo', 'bar' ];
*
* var opts = {
*     'returns': 'indices'
};
* var out = groupBy( arr, opts, indicator );
* // returns { 'b': [ 0, 1, 3 ], 'f': [ 2 ] }
*/
declare function groupIn<T = unknown>( collection: Collection<T>, options: IndicesOptions<T>, indicator: Indicator<T> ): IndicesResults;

/**
* Groups an object's own and inherited property values according to an indicator function.
*
* ## Notes
*
* -   When invoked, the indicator function is provided two arguments:
*
*     -   `value`: object value
*     -   `key`: object key
*
* -   The value returned by an indicator function should be a value which can be serialized as an object key.
*
* -   If provided an empty object with no prototype, the function returns an empty object.
*
* -   The function iterates over an object's own and inherited properties.
*
* -   Key iteration order is **not** guaranteed, and, thus, result order is **not** guaranteed.
*
* @param obj - input object
* @param options - function options
* @param options.thisArg - execution context
* @param options.returns - if `values`, values are returned; if `keys`, keys are returned; if `*`, both keys and values are returned (default: 'values')
* @param indicator - indicator function indicating which group an element in the input object belongs to
* @returns group results
*
* @example
* function indicator( v ) {
*     return v[ 0 ];
* }
*
* function Foo() {
*     this.a = 'beep';
*     this.b = 'boop';
*     return this;
* }
*
* Foo.prototype = Object.create( null );
* Foo.prototype.c = 'foo';
* Foo.prototype.d = 'bar';
*
* var obj = new Foo();
*
* var opts = {
*     'returns': 'keys'
* };
* var out = groupIn( obj, opts, indicator );
* // e.g., returns { 'b': [ 'a', 'b', 'd' ], 'f': [ 'c' ] }
*
* @example
* function indicator( v ) {
*     return v[ 0 ];
* }
*
* function Foo() {
*     this.a = 'beep';
*     this.b = 'boop';
*     return this;
* }
*
* Foo.prototype = Object.create( null );
* Foo.prototype.c = 'foo';
* Foo.prototype.d = 'bar';
*
* var obj = new Foo();
*
* var opts = {
*     'returns': '*'
* };
* var out = groupIn( obj, opts, indicator );
* // e.g., returns { 'b': [ [ 'a', 'beep' ], [ 'b', 'boop' ], [ 'd', 'bar' ] ], 'f': [ [ 'c', 'foo' ] ] }
*/

declare function groupIn<T = unknown>( collection: Collection<T>, options: IndicesAndValuesOptions<T>, indicator: Indicator<T> ): IndicesAndValuesResults<T>;


// EXPORTS //

export = groupIn;
