'use strict';

// MODULES //

var toString = require( './tostring.js' );


// TEST //

/**
* Attempts to serialize a value to a string.
*
* @param {*} value - value to test
* @returns {boolean} boolean indicating if a value can be serialized
*/
function test( value ) {
	try {
		toString.call( value );
		return true;
	} catch ( err ) {
		return false;
	}
} // end FUNCTION test()


// EXPORTS //

module.exports = test;
