'use strict';

/**
* Tests if a value is an empty array.
*
* @module @stdlib/utils/is-empty-array
*
* @example
* var isEmptyArray = require( '@stdlib/utils/is-empty-array' );
*
* var bool = isEmptyArray( [] );
* // returns true
*
* bool = isEmptyArray( [1,2,3] );
* // returns false

* bool = isEmptyArray( {} );
* // returns false
*/

// MODULES //

var isEmptyArray = require( './is_empty_array.js' );


// EXPORTS //

module.exports = isEmptyArray;
