'use strict';

var isFunctionArray = require( './../lib' );

function beep() {
	console.log( 'beep' );
}

function boop() {
	console.log( 'boop' );
}

var bool = isFunctionArray( [ beep, boop ] );
console.log( bool );
// => true

bool = isFunctionArray( [ beep, {} ] );
console.log( bool );
// => false

bool = isFunctionArray( [ [], {} ] );
console.log( bool );
// => false

bool = isFunctionArray( [ 'a', 'b' ] );
console.log( bool );
// => false

bool = isFunctionArray( [] );
console.log( bool );
// => false

bool = isFunctionArray( beep );
console.log( bool );
// => false

bool = isFunctionArray( null );
console.log( bool );
// => false
