'use strict';

// MODULES //

var tape = require( 'tape' );
var isNull = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isNull, 'function', 'main export is a function' );
	t.end();
});

tape( 'function returns `true` if provided `null`', function test( t ) {
	t.equal( isNull( null ), true, 'returns true' );
	t.end();
});

tape( 'function returns `false` if not provided `null`', function test( t ) {
	var values;
	var i;

	values = [
		0,
		[],
		'',
		function(){},
		false,
		{},
		NaN,
		undefined
	];

	for ( i = 0; i < values.length; i++ ) {
		t.equal( isNull( values[i] ), false, 'returns false when provided '+values[i] );
	}
	t.end();
});
