'use strict';

/**
* Test if a value is a plain object.
*
* @module @stdlib/utils/is-plain-object
*
* @example
* var isPlainObject = require( '@stdlib/utils/is-plain-object' );
*
* var bool = isPlainObject( {} );
* // returns true
*
* bool = isPlainObject( null );
* // returns false
*
* @example
* var isPlainObjectArray = require( '@stdlib/utils/is-plain-object' ).isPlainObjectArray;
*
* var bool = isPlainObjectArray( [{}, {}]  );
* // returns true
*
* bool = isPlainObjectArray( [{}, null] );
// returns false
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var arrayfun = require( '@stdlib/utils/tools/array-function' );
var isPlainObject = require( './is_plain_object.js' );


// METHODS //

setReadOnly( isPlainObject, 'isPlainObjectArray', arrayfun.create( isPlainObject ) );


// EXPORTS //

module.exports = isPlainObject;
