'use strict';

/**
* Tests if a value is a positive integer.
*
* @module @stdlib/utils/is-positive-integer
*
* @example
* var isPositiveInteger = require( '@stdlib/utils/is-positive-integer' );
*
* var bool = isPositiveInteger( 5.0 );
* // returns true
*
* bool = isPositiveInteger( new Number( 5.0 ) );
* // returns true
*
* bool = isPositiveInteger( -5.0 );
* // returns false
*
* bool = isPositiveInteger( 3.14 );
* // returns false
*
* bool = isPositiveInteger( null );
* // returns false
* @example
* // Use interface to check for positive integer primitives...
* var isPositiveInteger = require( '@stdlib/utils/is-positive-integer' ).isPrimitive;
*
* var bool = isPositiveInteger( 3.0 );
* // returns true
*
* bool = isPositiveInteger( new Number( 3.0 ) );
* // returns false
* @example
* // Use interface to check for positive integer objects...
* var isPositiveInteger = require( '@stdlib/utils/is-positive-integer' ).isObject;
*
* var bool = isPositiveInteger( 3.0 );
* // returns false
*
* bool = isPositiveInteger( new Number( 3.0 ) );
* // returns true
* @example
* // Use interface to check for positive integer arrays...
* var isPositiveInteger = require( '@stdlib/utils/is-positive-integer' ).isPositiveIntegerArray;
*
* var bool = isPositiveInteger( [ 3.0, new Number(3.0) ] );
* // returns true
*
* bool = isPositiveInteger( [ 3.0, '3.0' ] );
* // returns false
* @example
* // Use interface to check for primitive positive integer arrays...
* var isPositiveInteger = require( '@stdlib/utils/is-positive-integer' ).isPositivePrimitiveIntegerArray;
*
* var bool = isPositiveInteger( [ 1.0, 0.0, 10.0 ] );
* // returns false
*
* bool = isPositiveInteger( [ 1.0, 2.0, 10.0 ] );
* // returns true
*
* bool = isPositiveInteger( [ 3.0, new Number(1.0) ] );
* // returns false
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var arrayfun = require( '@stdlib/utils/tools/array-like-function' );
var isPositiveInteger = require( './generic.js' );
var isPrimitive = require( './primitive.js' );
var isObject = require( './object.js' );


// METHODS //

setReadOnly( isPositiveInteger, 'isPrimitive', isPrimitive );
setReadOnly( isPositiveInteger, 'isObject', isObject );
setReadOnly( isPositiveInteger, 'isPositiveIntegerArray', arrayfun.create( isPositiveInteger ) );
setReadOnly( isPositiveInteger, 'isPositivePrimitiveIntegerArray', arrayfun.create( isPrimitive ) );


// EXPORTS //

module.exports = isPositiveInteger;
