'use strict';

// MODULES //

var tape = require( 'tape' );
var isRegExpString = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isRegExpString, 'function', 'main export is a function' );
	t.end();
});

tape( 'function returns `true` if provided a regular expression string', function test( t ) {
	t.equal( isRegExpString( '/beep/' ), true, 'returns true' );
	t.equal( isRegExpString( '/beep/gim' ), true, 'returns true' );
	t.end();
});

tape( 'function returns `false` if not provided a regular expression string', function test( t ) {
	var values;
	var i;

	values = [
		5,
		[],
		'5',
		'/beep',
		'beep/',
		'beep/gim',
		/\.+/,
		new RegExp( 'beep', 'gim' ),
		function(){},
		null,
		NaN,
		{},
		undefined
	];
	for ( i = 0; i < values.length; i++ ) {
		t.equal( isRegExpString( values[i] ), false, 'returns false when provided '+values[i]  );
	}
	t.end();
});
