'use strict';

// MODULES //

var getKeys = require( 'object-keys' ).shim();
var isObjectLike = require( '@stdlib/assert/is-object-like' );


// MAIN //

/**
* Returns an array of an object's own enumerable property values.
*
* @param {ObjectLike} obj - input object
* @throws {TypeError} must provide an object-like value
* @returns {Array} value array
*
* @example
* var obj = {
*     'beep': 'boop',
*     'foo': 'bar'
* };
*
* var vals = objectValues( obj );
* // e.g., returns [ 'boop', 'bar' ]
*/
function objectValues( obj ) {
	var keys;
	var out;
	var i;
	if ( !isObjectLike( obj ) ) {
		throw new TypeError( 'invalid input argument. Must provide an object-like value. Value: `'+obj+'`.' );
	}
	keys = getKeys( obj );
	out = new Array( keys.length );
	for ( i = 0; i < keys.length; i++ ) {
		out[ i ] = obj[ keys[i] ];
	}
	return out;
} // end FUNCTION objectValues()


// EXPORTS //

module.exports = objectValues;
