'use strict';

var prepend = require( './../lib' );

var arr;
var i;
var j;

arr = new Float64Array();
for ( i = 0; i < 100; i++ ) {
	j = i * 3;
	arr = prepend( arr, [ j+2, j+1, j ] );
}
console.log( arr );
console.log( 'View Length: %d', arr.byteLength );
console.log( 'Buffer Length: %d', arr.buffer.byteLength );
