'use strict';

var shift = require( './../lib' );

var arr;
var out;
var i;

arr = new Float64Array( 100 );
for ( i = 0; i < 100; i++ ) {
	out = shift( arr );
	arr = out[ 0 ];
	console.log( 'Length: %d', arr.length );
}
console.log( arr );
console.log( 'View Length: %d', arr.byteLength );
console.log( 'Buffer Length: %d', arr.buffer.byteLength );
