'use strict';

// MODULES //

var isArray = require( '@stdlib/utils/is-array' );


// EVERY //

/**
* FUNCTION: every( fcn, value )
*	Tests each array element.
*
* @param {Function} fcn - function to apply
* @param {*} value - value to test
* @returns {Boolean} true if value is an array for which all elements pass; otherwise, false
*/
function every( fcn, value ) {
	if ( !isArray( value ) ) {
		return false;
	}
	var len = value.length;
	var i;
	if ( !len ) {
		return false;
	}
	for ( i = 0; i < len; i++ ) {
		if ( fcn( value[ i ] ) === false ) {
			return false;
		}
	}
	return true;
} // end FUNCTION every()


// EXPORTS //

module.exports = every;
