'use strict';

// MODULES //

var tape = require( 'tape' );
var noop = require( '@stdlib/utils/noop' );
var isEven = require( './fixtures/iseven.js' );
var every = require( './../lib/every.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof every, 'function', 'main export is a function' );
	t.end();
});

tape( 'function throws an error if not provided a test function', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		5,
		NaN,
		true,
		null,
		undefined,
		[],
		{}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws TypeError' );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			every( value, [1,2,3] );
		};
	}
});

tape( 'function returns `false` if not provided a typed array', function test( t ) {
	var values;
	var i;

	values = [
		5,
		NaN,
		true,
		null,
		undefined,
		[ 2, 4, 6 ],
		{},
	];

	for ( i = 0; i < values.length; i++ ) {
		t.equal( every( noop, values[ i ] ), false, 'returns false' );
	}
	t.end();
});

tape( 'function returns `false` for an empty typed array', function test( t ) {
	t.equal( every( noop, new Int32Array() ), false, 'returns false' );
	t.end();
});

tape( 'function tests elements of a typed array', function test( t ) {
	var arr;
	var out;

	arr = new Float64Array( [ 1, 1, 2, 4 ] );

	out = every( isEven, arr );
	t.equal( out, false );

	arr = new Float64Array( [ 2, 2, 2, 4 ] );

	out = every( isEven, arr );
	t.equal( out, true );
	t.end();
});
