'use strict';

var wrap = require( './../lib' );

function beep( str ) {
	if ( typeof str !== 'string' ) {
		throw new TypeError( 'invalid input argument. Must provide a string primitive. Value: `' + str + '`.' );
	}
	return 'beep ' + str;
}

function boop( str, clbk ) {
	if ( typeof str !== 'string' ) {
		throw new TypeError( 'invalid input argument. Must provide a string primitive. Value: `' + str + '`.' );
	}
	setTimeout( done, 1000 );

	function done() {
		if ( str !== 'beep' ) {
			throw new Error( 'invalid input argument. String must equal `beep`. Value: `' + str + '`.' );
		}
		clbk( str + ' boop' );
	}
}

function done( str ) {
	if ( str !== 'beep boop' ) {
		throw new Error( 'huh?' );
	}
}

var out;
var f;

// Synchronous...
f = wrap( beep );

out = f( 'boop' );
console.log( out );
// => 'beep boop'

out = f( null );
console.log( out.message );
// => '...'


// Asynchronous...
f = wrap( boop );

out = f( 'beep', done );
console.log( out );
// => undefined

out = f( 'foo', done );
console.log( out );
// returns undefined

// TODO: handle browser environment, using `window.onerror`

process.on( 'uncaughtException', onException );

function onException( err ) {
	// We should only throw once...
	process.removeListener( 'uncaughtException', onException );
	console.log( err.message );
}
