'use strict';

// MODULES //

var ctorName = require( '@stdlib/utils/constructor-name' );


// TYPEOF //

/**
* Determines a value's type.
*
* @param {*} v - input value
* @returns {string} string indicating the value's type
*/
function typeOf( v ) {
	return ctorName( v ).toLowerCase();
} // end FUNCTION typeOf()


// EXPORTS //

module.exports = typeOf;
