'use strict';

var curry = require( '@stdlib/utils/curry' );
var uncurry = require( './../lib' );

var uncurried;
var curried;
var out;
var i;

function add( x, y, z, w, t, s ) {
	return x + y + z + w + t + s;
}

out = add( 0, 10, 20, 30, 40, 50 );
console.log( out );
// => 150

// Transform `add` into a curried function:
curried = curry( add );
out = curried;
for ( i = 0; i < add.length; i++ ) {
	out = out( i*10 );
}
console.log( out );
// => 150

// Uncurry a curried function:
uncurried = uncurry( curried );

out = uncurried( 0, 10, 20, 30, 40, 50 );
console.log( out );
// => 150
